import React, { useState, useEffect } from 'react';
import axios from 'axios';
import RecipeDisplay from '../components/DisplayRecipe';

const ViewRecipes = () => {
    const [recipe, setRecipe] = useState([]);

    useEffect(() => {
        // Function to fetch recipe data from backend
        const fetchRecipe = async () => {
          try {
            const response = await axios.get('http://localhost:5001/generate-recipe');
            setRecipe(response.data); // Set fetched recipe data to state
            console.log(response.data, 'hello');
          } catch (error) {
            console.error('Error fetching recipe:', error);
          }
        };

        fetchRecipe(); // Invoke the fetch function when component mounts
      }, []); // Empty dependency array ensures useEffect runs only once

    return (
        <div>
            <h1>View Recipes Page</h1>
            <div>
                <RecipeDisplay recipe={recipe} />
            </div>
        </div>
    );
}

export default ViewRecipes;
