import React, { useState } from 'react';

const GenerateRecipeForm = ({ onGenerate }) => {
    const [prompt, setPrompt] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Call parent component's function to handle generation
        onGenerate(prompt);
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Enter a prompt:
                <input
                    type="text"
                    value={prompt}
                    onChange={(e) => setPrompt(e.target.value)}
                    className="bg-gray-200 appearance-none border-2 border-gray-200 rounded w-1/2 py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500"
                />
            </label>
            <button type="submit">Generate Recipe</button>
        </form>
    );
}

export default GenerateRecipeForm;
