import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Assuming axios is used for API calls

const DisplayRecipe = () => {
  const [recipe, setRecipe] = useState(null); // State to store recipe data

  useEffect(() => {
    // Function to fetch recipe data from backend
    const fetchRecipe = async () => {
      try {
        const response = await axios.get('http://localhost:5001/generate-recipe'); // Adjust URL as per your backend endpoint
        setRecipe(response.data); // Set fetched recipe data to state
      } catch (error) {
        console.error('Error fetching recipe:', error);
      }
    };

    fetchRecipe(); // Invoke the fetch function when component mounts
  }, []); // Empty dependency array ensures useEffect runs only once

  return (
    <div className="recipe-display">
      {recipe ? (
        <div>
          <h2>{recipe.name}</h2>
          <p>{recipe.description}</p>
          <h3>Ingredients:</h3>
          <ul>
            {recipe.ingredients.map((ingredient, index) => (
              <li key={index}>{ingredient.name} - {ingredient.amount} {ingredient.unit}</li>
            ))}
          </ul>
          <h3>Instructions:</h3>
          <ol>
            {recipe.instructions.map((instruction, index) => (
              <li key={index}>{instruction.step}</li>
            ))}
          </ol>
          <h3>Tips:</h3>
          <ul>
            {recipe.tips.map((tip, index) => (
              <li key={index}>{tip}</li>
            ))}
          </ul>
        </div>
      ) : (
        <p>Loading recipe...</p>
      )}
    </div>
  );
};

export default DisplayRecipe;
