import React, { useState, useEffect } from 'react';
import RecipeDisplay from '../components/DisplayRecipe';

const ViewRecipes = () => {
    const [recipes, setRecipes] = useState([]);

    useEffect(() => {
        const fetchRecipes = async () => {
            try {
                const response = await fetch('http://localhost:5001/generate-recipe');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setRecipes(data); // Update state with fetched data
            } catch (error) {
                console.error('Error fetching recipes:', error);
            }
        };

        fetchRecipes();
    }, []); 

    return (
        <div>
            <h1>View Recipes Page</h1>
            <div>
                <RecipeDisplay recipe={recipes} />
            </div>
        </div>
    );
}

export default ViewRecipes;
